INSERT INTO `paginas` VALUES ("87", "2017-05-15 20:35:13", "0000-00-00 00:00:00", NULL, "1", NULL, "1", NULL, "Enlaces de Interés", "<p><a href=\"http://rpp.pe\" target=\"_blank\">RPP Noticias&nbsp;</a><br />\nCadena radial, la m&aacute;s importante del pa&iacute;s y que ahora tambi&eacute;n ofrece sus contenidos v&iacute;a cable tv.</p>\n\n<p><a href=\"http://elcomercio.pe\" target=\"_blank\">El Comercio</a>&nbsp;<br />\nPeri&oacute;dico que ha sido testigo de una gran parte de la vida republicana del pa&iacute;s, y que informa de todo lo que sucede en el Per&uacute; y en el mundo.</p>\n\n<p><a href=\"http://peru.info/es-pe\" target=\"_blank\">Marca Per&uacute;</a>&nbsp;<br />\nSitio web dedicado a dar a conocer y difundir lo que es el Per&uacute; y lo que ofrece al mundo.</p>\n\n<p><a href=\"https://peru.info/es-pe/\" target=\"_blank\">La Mula&nbsp;</a><br />\nPortal donde encontrar&aacute; blogs, columnas de opini&oacute;n y art&iacute;culos de diferentes personalidades reconocidas en el medio y que escriben sobre diversos temas de actualidad en el Per&uacute; y en el mundo.</p>\n", NULL, "24", NULL);
