INSERT INTO `paginas` VALUES ("88", "2017-05-15 20:35:48", "2017-05-15 20:36:27", NULL, "1", "pag_1494898587_437x262.jpg", "1", NULL, "Traducciones", "<p><span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Servicio de traducci&oacute;n de documentos de tipo personal, administrativo o profesional; y en otras &aacute;reas especializadas como medicina, ingenier&iacute;a, comercio, turismo, etc.</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Somos un equipo de profesionales con amplia experiencia en el campo de la traducci&oacute;n y en el manejo y gesti&oacute;n de proyectos de traducci&oacute;n, le ofrecemos seriedad, puntualidad y calidad en sus traducciones en las &aacute;reas m&aacute;s diversas y cumpliendo con los plazos de entrega establecidos.</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">&bull; Traducciones simples, certificadas y oficiales.</span><br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">&bull; Rapidez, exactitud, puntualidad, confidencialidad.</span><br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">&bull; Textos jur&iacute;dicos, econ&oacute;micos, contables, administrativos, t&eacute;cnicos, cientificos, en ingl&eacute;s, alem&aacute;n, franc&eacute;s, portugu&eacute;s.</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Telf. 99112-7986 / 99215-4786</span><br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">E-mail:&nbsp;</span><a href=\"mailto:richfel@yahoo.com\" target=\"_blank\">richfel@yahoo.com</a><span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">&nbsp;/&nbsp;</span><a href=\"mailto:terranova.traducciones@yahoo.com\" target=\"_blank\">terranova.traducciones@yahoo.com</a></p>\n", NULL, "24", NULL);
