INSERT INTO `paginas` VALUES ("89", "2017-05-18 11:47:19", "0000-00-00 00:00:00", NULL, "1", "pag_1495126039_437x209.jpg", "1", NULL, "Cursos de Inglés", "<p><span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">En un mundo tan competitivo y exigente como el que se vive en la actualidad, se requiere profesionales altamente calificados y con un manejo y dominio de idiomas.&nbsp;</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Terranova le ofrece la soluci&oacute;n a sus requerimientos, contamos con cursos de nivel B&aacute;sico, Intermedio y Avanzado, usamos una metodolog&iacute;a moderna, din&aacute;mica y siguiendo los est&aacute;ndares m&aacute;s altos de calidad para que el alumno pueda alcanzar su meta que es el manejo del idioma ingl&eacute;s en el tiempo m&aacute;s breve.&nbsp;</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Contamos con material especialmente seleccionado y preparado para el desarrollo de las clases con asesor&iacute;a personalizada y seguimiento del avance y progreso del alumno.&nbsp;</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Nuestros profesores est&aacute;n plenamente calificados y tienen una amplia experiencia en el campo de la docencia de idiomas.&nbsp;</span><br />\n<br />\n<span style=\"color:rgb(0, 0, 0); font-family:arial,century gothic,helvetica,sans-serif\">Tambi&eacute;n nos enfocamos en clases para ni&ntilde;os y j&oacute;venes utilizando un enfoque comunicacional e interactivo centrado en las necesidades y requerimientos del alumno con asesor&iacute;a y seguimiento personalizado para el desarrollo de las tareas escolares y otros tipos de trabajos.&nbsp;</span></p>\n", NULL, "24", NULL);
